import React, { useState } from 'react';
import { validateEmail } from '../../utils/helpers';
import './style.css';

function ContactMe() {
  const [formState, setFormState] = useState({ name: '', email: '', message: '' });
  const [errorMessage, setErrorMessage] = useState('');

  const { name, email, message } = formState;

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!errorMessage) {
      console.log('Submitting form...');
      console.log(formState);
      // Add code to submit form here
    }
  };

  const handleChange = (e) => {
    if (e.target.name === 'email') {
      const isValid = validateEmail(e.target.value);
      if (!isValid) {
        setErrorMessage('Your email is invalid.');
      } else {
        setErrorMessage('');
      }
    } else {
      if (!e.target.value.length) {
        setErrorMessage(`${e.target.name} is required.`);
      } else {
        setErrorMessage('');
      }
    }

    setFormState({ ...formState, [e.target.name]: e.target.value });
  };

  return (
    <section className='contactMe' id='contactMe'>
      <h1 data-testid='h1tag'>Contact me</h1>
      <form id='contact-form' onSubmit={handleSubmit}>
        <div>
          <label htmlFor='name'>Name:</label>
          <input type='text' name='name' className='form-input' defaultValue={name} onBlur={handleChange} required aria-label='Name' />
        </div>
        <div>
          <label htmlFor='email'>Email address:</label>
          <input type='email' name='email' className='form-input' defaultValue={email} onBlur={handleChange} required aria-label='Email address' />
        </div>
        <div>
          <label htmlFor='message'>Message:</label>
          <br />
          <textarea name='message' className='form-textarea' rows='5' defaultValue={message} onBlur={handleChange} required aria-label='Message'></textarea>
        </div>
        {errorMessage && (
          <div>
            <p className='error-text'>{errorMessage}</p>
          </div>
        )}
        <button data-testid='button' type='submit'>
          Submit
        </button>
      </form>
    </section>
  );
}

export default ContactMe;
